import gql from 'graphql-tag';
import { actionPermissionGQLFields, } from './actionPermissions.fields.js';
export function getPermissionsGQLQuery(fields) {
    return gql `
    query GetPermissions($contexts: [PermissionsContextInput!] = []) {
      permissions(contexts: $contexts) {
        contextAwareActions {
          context
          entityActions {
            id
            actions {
              ${actionPermissionGQLFields(fields)}
            }
          }
        }
      }
    }
  `;
}
